// Simple Faker.js implementation for AI FormFill extension
(function (global) {
  const faker = {
    setLocale: function (locale) {
      this.locale = locale || 'en';
      return this;
    },

    random: {
      number: function (options) {
        options = options || {};
        const min = options.min || 0;
        const max = options.max || 100;
        return Math.floor(Math.random() * (max - min + 1)) + min;
      },

      arrayElement: function (array) {
        array = array || [];
        return array[Math.floor(Math.random() * array.length)];
      }
    },

    name: {
      firstName: function () {
        const names = ['John', 'Jane', 'Michael', 'Emily', 'David', 'Sarah', 'James', 'Jessica', 'Robert', 'Jennifer'];
        return faker.random.arrayElement(names);
      },

      lastName: function () {
        const names = ['Smith', 'Johnson', 'Williams', 'Brown', 'Jones', 'Miller', 'Davis', 'Garcia', 'Rodriguez', 'Wilson'];
        return faker.random.arrayElement(names);
      },

      fullName: function () {
        return faker.name.firstName() + ' ' + faker.name.lastName();
      }
    },

    internet: {
      email: function () {
        const domains = ['gmail.com', 'yahoo.com', 'hotmail.com', 'example.com', 'outlook.com'];
        return faker.name.firstName().toLowerCase() + '.' +
          faker.name.lastName().toLowerCase() +
          '@' + faker.random.arrayElement(domains);
      },

      userName: function () {
        return faker.name.firstName().toLowerCase() + faker.random.number({ min: 10, max: 99 });
      },

      url: function () {
        const domains = ['example.com', 'test.com', 'domain.com', 'site.net', 'website.org'];
        return 'https://www.' + faker.random.arrayElement(domains);
      }
    },

    phone: {
      number: function () {
        return '555-' + faker.random.number({ min: 100, max: 999 }) + '-' + faker.random.number({ min: 1000, max: 9999 });
      }
    },

    location: {
      streetAddress: function () {
        const num = faker.random.number({ min: 100, max: 9999 });
        const streets = ['Main St', 'Oak Ave', 'Maple Rd', 'Park Blvd', 'Washington St', 'Cedar Ln'];
        return num + ' ' + faker.random.arrayElement(streets);
      },

      city: function () {
        const cities = ['New York', 'Los Angeles', 'Chicago', 'Houston', 'Phoenix', 'Philadelphia', 'San Antonio', 'San Diego', 'Dallas', 'Austin'];
        return faker.random.arrayElement(cities);
      },

      state: function () {
        const states = ['AL', 'AK', 'AZ', 'AR', 'CA', 'CO', 'CT', 'DE', 'FL', 'GA', 'HI', 'ID', 'IL', 'IN', 'IA', 'KS', 'KY', 'LA', 'ME', 'MD', 'MA', 'MI', 'MN', 'MS', 'MO', 'MT', 'NE', 'NV', 'NH', 'NJ', 'NM', 'NY', 'NC', 'ND', 'OH', 'OK', 'OR', 'PA', 'RI', 'SC', 'SD', 'TN', 'TX', 'UT', 'VT', 'VA', 'WA', 'WV', 'WI', 'WY'];
        return faker.random.arrayElement(states);
      },

      zipCode: function () {
        return faker.random.number({ min: 10000, max: 99999 }).toString();
      },

      country: function () {
        const countries = ['United States', 'Canada', 'United Kingdom', 'Australia', 'Germany', 'France', 'Italy', 'Spain', 'Japan', 'China'];
        return faker.random.arrayElement(countries);
      }
    },

    company: {
      name: function () {
        const companies = ['Acme Inc', 'Global Tech', 'Innovative Solutions', 'Strategic Systems', 'Dynamic Enterprises', 'Premier Services', 'Elite Corporation', 'Superior Products', 'Precision Manufacturing', 'Advanced Technology'];
        return faker.random.arrayElement(companies);
      }
    },

    finance: {
      creditCardNumber: function () {
        let result = '';
        for (let i = 0; i < 4; i++) {
          result += faker.random.number({ min: 1000, max: 9999 }).toString();
          if (i < 3) result += ' ';
        }
        return result;
      },

      creditCardCVV: function () {
        return faker.random.number({ min: 100, max: 999 }).toString();
      }
    },

    date: {
      future: function () {
        const today = new Date();
        const futureDate = new Date(today);
        futureDate.setFullYear(today.getFullYear() + faker.random.number({ min: 1, max: 5 }));
        return futureDate;
      }
    }
  };

  // Initialize with default locale
  faker.setLocale('en');

  // Expose to window object
  global.faker = faker;
})(typeof window !== 'undefined' ? window : this);